targets.query:
from(bucket: "mybucket")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "spans")
  // Nur die Felder ausw채hlen, die wir wirklich brauchen
  |> filter(fn: (r) => r._field == "duration_nano" or r._field == "span.name")
  // Daten umwandeln: Zeilen werden zu Spalten
  |> pivot(
    rowKey:["_time", "trace_id", "span_id"],
    columnKey: ["_field"],
    valueColumn: "_value"
  )
  // Nur die Zeilen behalten, die einen Endpunkt-Namen haben
  |> filter(fn: (r) => exists r["span.name"])
  // Nach Endpunkt-Namen gruppieren
  |> group(columns: ["span.name"])
  // Den Durchschnitt der Dauer f체r jede Gruppe berechnen
  |> mean(column: "duration_nano")
  // Ergebnis f체r bessere Lesbarkeit umrechnen und umbenennen
  |> map(fn: (r) => ({
    Endpoint: r["span.name"],
    // Umrechnung von Nanosekunden in Millisekunden
    avg_duration_ms: float(v: r.duration_nano) / 1000000.0
  }))
  // Nur die relevanten Spalten f체r die Tabelle behalten
  |> keep(columns: ["Endpoint", "avg_duration_ms"])
